
var btnNewMov=document.getElementById("js-play");function onPlayerReady(event){try{btnNewMov.onclick=function(){if($("#js-new").size()>0){var _id=$("#js-new").find(".c-mv-item").attr("data-ytid");eval("var newMovie = "+_id+";");newMovie.playVideo();}};}catch(e){}gaPlayers.push(event.target);var iframeID=event.target.getIframe().id;gaMovieState[iframeID]=false;}function onPlayerStateChange(d){var e=d.target.getIframe().id;var b=$("#"+e).attr("data-ga");var c=b+"_start";var a=b+"_complete";if(d.data==YT.PlayerState.PLAYING){if(!gaMovieState[e]){ga("send","pageview",c);}if(!gaViewTime[e]){gaViewTime[e]=0;}CountSecTimer=setInterval("gaCountSec('"+e+"')",1000);gaMovieState[e]=true;}if(d.data==YT.PlayerState.ENDED){ga("send","pageview",a);if(CountSecTimer){clearInterval(CountSecTimer);}gaViewTime[e]=false;gaMovieState[e]=false;}if(d.data==YT.PlayerState.PAUSED){if(CountSecTimer){clearInterval(CountSecTimer);}}}